// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {Context} from "@openzeppelin/contracts/utils/Context.sol";

import {IRouterClient} from "@chainlink/contracts-ccip/src/v0.8/ccip/interfaces/IRouterClient.sol";
import {Client} from "@chainlink/contracts-ccip/src/v0.8/ccip/libraries/Client.sol";

contract KanvasCCIP {
    address private _kanvas;
    address private _router;
    uint64 private constant _avaxSelector = 2664363617261496610;

    constructor(address kanvas_, address router_) {
        _kanvas = kanvas_;
        _router = router_;
    }

    // this functions call the update function for the avax core contract
    function update(uint256 appId, address to, bytes memory data) external {
        // Create an EVM2AnyMessage struct in memory with necessary information for sending a cross-chain message
        Client.EVM2AnyMessage memory evm2AnyMessage = Client.EVM2AnyMessage({
            // ABI-encoded receiver address
            receiver: abi.encode(to),
            // ABI-encoded string message
            data: abi.encode(appId, data),
            // Empty array indicating no tokens are being sent
            tokenAmounts: new Client.EVMTokenAmount[](0),
            // Additional arguments, setting gas limit and non-strict sequency mode
            extraArgs: Client._argsToBytes(
                Client.EVMExtraArgsV1({gasLimit: 400_000, strict: false})
            ),
            // Setting feeToken to zero address, indicating native asset will be used for fees
            feeToken: address(0)
        });

        // Initialize a router client instance to interact with cross-chain router
        IRouterClient router = IRouterClient(_router);

        // Get the fee required to send the message
        uint256 fees = router.getFee(_avaxSelector, evm2AnyMessage);

        // Send the message through the router and store the returned message ID
        router.ccipSend{value: fees}(_avaxSelector, evm2AnyMessage);
    }
}
